class MarverService{
    getResource = async (url) =>{
        let res = await fetch(url);

        if(!res.ok){
            throw new Error(`Could not fetch @{url}, status ${res.status}`);
        }
        return await res.json()
    }
    getAllCharacters = () =>{
        return this.getResource('https://gateway.marvel.com:443/v1/public/characters?limit=9&offset=210&apikey=79e891de74347d7a9490aa8fa3c8739e')
    }
    getCharacter = async(id) =>{
        const res=await this.getResource(`https://gateway.marvel.com:443/v1/public/characters/${id}?apikey=79e891de74347d7a9490aa8fa3c8739e`)
        return this._transformCharacter(res);
    }   
    _transformCharacter=(res)=>{
        return{
            name:res.data.results[0].name,
            description:res.data.results[0].description,
            thumbnail:res.data.results[0].thumbnail.path + '.' + res.data.results[0].thumbnail.extension,
            homepage:res.data.results[0].urls[0].url,
            wiki:res.data.results[0].urls[1].url
        }
    }
}
export default MarverService;
